import React, { Component } from "react";
// const [name, setName] = useState("");

export default class HomeInputFields extends Component {
  constructor(props) {
    super(props);
    this.state = { posts: [] };
    // this.setState = () => {
    //   this.posts = posts;
    // };
  }
  onChangeName = (event) => {
    // setName(event.target.value);
    // this.props.submitInputData(event.target.name.value);
  };
  onAddButtonTapped = (event) => {
    const post = { id: 1, title: event.value };
    this.props.submitInputData(post);
  };
  render() {
    return (
      <div>
        <input
          type="name"
          onChange={this.onChangeName}
          value={this.state.posts}
        ></input>
        <button onClick={this.onAddButtonTapped}>add</button>
        {/* <h2>{name}</h2> */}
      </div>
    );
  }
}
