import React, { Component } from "react";
// const [name, setName] = useState("");

export default class HomeInputFields extends Component {
  constructor(props) {
    super(props);
    this.onAddButtonTapped.bind(this);
    this.state = { posts: [] };
    // this.setState = () => {
    //   this.posts = posts;
    // };
  }
  onChangeName = (event) => {


    this.setState({
      name:event.target.value,
    })
    // setName(event.target.value);
    // this.props.submitInputData(event.target.name.value);
  };
  onAddButtonTapped = () => {
    // const post = [{ id: 1, title: this.state.posts.title }];
    this.setState({
      posts: [
        {
          id: Math.random()
            .toString(10000)
            .replace("0.", "todo" || ""),
          title:this.state.name,
        },
      ],
    });
    this.props.postData.push(this.state.posts);
    this.props.reloadHomeDataFromInput(this.props.postData);
  };
  render() {
    return (
      <div>
        <input
          type="name"
          onChange={this.onChangeName}
          value={this.state.posts.title}
        ></input>
        <button onClick={this.onAddButtonTapped}>add</button>
        {/* <h2>{this.state.name}</h2> */}
      </div>
    );
  }
}
