import React, { Component } from "react";
import HomeInputFields from "../Components/Home/HomeInputFields";
// import { useState } from "react";
export default class Home extends Component {
  constructor(props) {
    super(props);
    const posts = [];
    this.state = { posts: [],anotherPostData: [{id:1,title:"hello"},{id:2,title:"helloworld"}]};
    this.setState = () => {
      this.posts = posts;
    };
    this.reloadHomeDataFromInput = this.reloadHomeDataFromInput.bind(this);
  }

  render() {
    // const { posts } = this.state;
    return (
      <div className="m-auto mx-32 h-64 rounded-lg">
        <div className="text-center text-3xl content-center justify-center">
          Home page
        </div>
        <HomeInputFields
          submitInputData={this.reloadHomeDataFromInput}
          postData={this.state.posts}
        ></HomeInputFields>

        <ul>
          {this.state.anotherPostData.map((post) => (
            <li key={post.id}>{post.title}</li>
          ))}
        </ul>
      </div>
    );
  }
  reloadHomeDataFromInput = (inputData) => {
    console.log("ok call back");


    this.setState({
      posts:[],
      anotherPostData: [{id:1,title:"hello"},{id:2,title:"helloworld -alien"}],
    });
    console.log(inputData);
    console.log(this.state.posts);
  };
}
